mapper.select:
SELECT A_DETAIL AS aDetail, PROGRESS AS progress
FROM ATTENDANCE 
WHERE A_NO = #{aNo}

mapper.update:
UPDATE ATTENDANCE
SET A_DETAIL = #{aDetail},
PROGRESS = #{progress, jdbcType=VARCHAR}
WHERE A_NO = #{aNo}

UPDATE ATTENDANCE
SET A_NO = #{aNo},
PROGRESS = #{progress, jdbcType=VARCHAR}
WHERE A_NO = #{aNo}
